import { useNumpad } from '../useNumpad';
import styled ,{ css }from 'styled-components';

const NumpadComponent = (() =>{

    const {handleClick, input, handleAnswer, handleClear}= useNumpad();

    const Container = styled.div`
        text-align: center;
    `
    const Card = styled.div`
        box-shadow: 0 4px 8px 0 rgba(255, 0, 85, 0.2),0 10px 8px 0 rgba(255, 6, 89, 0.2);
        max-width: 360px;
        margin: 10px;
        padding: 5px;
        text-align: center;
        background-color: #333;
        border-radius: 20px;
        transition:.4s;
    `
    const Screen = styled.div`
        height: 40px;
        background-color: #fafcfc;
        margin: 10px;
        padding: 10px 10px 0 10px;
        color: #2c2730;
        text-align: right;
        border-radius: 20px;

    `
    const Button = styled.button`
        background: #ebc2d0;
        border-radius: 50%;
        width: 50px;
        height: 50px;
        border: 2px solid palevioletred;
        color: #db7093;
        font-size: 22px;
        margin: 15px 10px 15px 10px;
        padding: 2px;
        -webkit-box-shadow: 0 0 17px 3px #db70ae,0 0 4px 2px #db70ae;
        box-shadow: 0 0 17px 3px #db70ae,0 0 4px 2px #db70ae;
    `
    const LongButton = styled(Button)`
        width: 120px;
        border-radius: 50px;
    `

    return (
        <Container> 
            <Card>
            <Screen>
                {input}
            </Screen>
            <div className="row">
            <LongButton onClick={handleClear} >C</LongButton>
            <Button value="%" onClick={handleClick}>%</Button>
            <Button value="/" onClick={handleClick}>/</Button>
            </div>
            <div className="row">
            <Button value="7" onClick={handleClick} >7</Button>
            <Button value="8" onClick={handleClick} >8</Button>
            <Button value="9" onClick={handleClick} >9</Button>
            <Button value="*" onClick={handleClick} >X</Button>
            </div>
            <div className="row">
            <Button value="4" onClick={handleClick} >4</Button>
            <Button value="5" onClick={handleClick} >5</Button>
            <Button value="6" onClick={handleClick} >6</Button>
            <Button value="-" onClick={handleClick} >-</Button>
            </div>
            <div className="row">
            <Button value="1" onClick={handleClick} >1</Button>
            <Button value="2" onClick={handleClick} >2</Button>
            <Button value="3" onClick={handleClick} >3</Button>
            <Button value="+" onClick={handleClick} >+</Button>
            </div>
            <div className="row">
            <LongButton value="0" onClick={handleClick} >0</LongButton>
            <Button value="." onClick={handleClick} >.</Button>
            <Button onClick={handleAnswer} > Ans </Button>
            </div>
            </Card>
        </Container>
)
})

export default NumpadComponent
