import { useNumpad } from '../useNumpad';
import styled ,{ css }from 'styled-components';

const NumpadComponent = (() =>{

    const {handleClick, input, handleAnswer, handleClear}= useNumpad();

    const Container = styled.div`
        text-align: center;
    `
    const Card = styled.div`
        box-shadow: 0 4px 8px 0 rgba(51, 50, 50, 0.2);
        max-width: 360px;
        margin: 10px;
        padding: 5px;
        text-align: center;
        background-color: #eaecec;
        border-radius: 20px;
        ::before{
            position: absolute;
            left: 0;
            top: 0;
            width: 100%;
            height: 100%;
            -webkit-box-shadow: 0 0 17px 3px #ffff01,0 0 4px 2px #ffff01;
             box-shadow: 0 0 17px 3px #ffff01,0 0 4px 2px #ffff01;
            z-index: -1;
            -webkit-animation-name: half-yellow-shadow;
            animation-name: half-yellow-shadow;
            -webkit-animation-timing-function: ease;
            animation-timing-function: ease;
            -webkit-animation-duration: 5s;
            animation-duration: 5s;
            -webkit-animation-iteration-count: infinite;
            animation-iteration-count: infinite;
            border-radius: 5px;
            -webkit-border-radius: 5px;
            -moz-border-radius: 5px;
            -ms-border-radius: 5px;
            -o-border-radius: 5px;
        }
        ::after{
            position: absolute;
            right: 0;
            bottom: 0;
            width: 50%;
            height: 100%;
            -webkit-box-shadow: 0 0 17px 3px #0ff,0 0 4px 2px #0ff;
            box-shadow: 0 0 17px 3px #0ff,0 0 4px 2px #0ff;
            z-index: -1;
            -webkit-animation-name: half-cyan-shadow;
            animation-name: half-cyan-shadow;
            -webkit-animation-timing-function: ease;
            animation-timing-function: ease;
            -webkit-animation-duration: 5s;
            animation-duration: 5s;
            -webkit-animation-iteration-count: infinite;
            animation-iteration-count: infinite;
            border-radius: 5px;
            -webkit-border-radius: 5px;
            -moz-border-radius: 5px;
            -ms-border-radius: 5px;
            -o-border-radius: 5px;
        }
    `
    const Screen = styled.div`
        height: 40px;
        background-color: #fafcfc;
        margin: 10px;
        padding: 10px 10px 0 10px;
        color: #070707;
        text-align: right;
        border-radius: 20px;

    `
    const Button = styled.button`
        background: transparent;
        border-radius: 50%;
        width: 50px;
        height: 50px;
        border: 2px solid palevioletred;
        color: #db7093;
        margin: 15px 10px 15px 10px;
        padding: 2px;
        -webkit-box-shadow: 0 0 17px 3px #db70ae,0 0 4px 2px #db70ae;
        box-shadow: 0 0 17px 3px #db70ae,0 0 4px 2px #db70ae;
    `
    const LongButton = styled(Button)`
        width: 120px;
        border-radius: 50px;
    `
    return (
        <Container> 
            <Card>
                <h2>Faiichan Calculate</h2>
            <Screen>
                {input}
            </Screen>
            <div className="row">
            <LongButton onClick={handleClear} >C</LongButton>
            <Button value="%" onClick={handleClick}>%</Button>
            <Button value="/" onClick={handleClick}>/</Button>
            </div>
            <div className="row">
            <Button value="7" onClick={handleClick} >7</Button>
            <Button value="8" onClick={handleClick} >8</Button>
            <Button value="9" onClick={handleClick} >9</Button>
            <Button value="*" onClick={handleClick} >X</Button>
            </div>
            <div className="row">
            <Button value="4" onClick={handleClick} >4</Button>
            <Button value="5" onClick={handleClick} >5</Button>
            <Button value="6" onClick={handleClick} >6</Button>
            <Button value="-" onClick={handleClick} >-</Button>
            </div>
            <div className="row">
            <Button value="1" onClick={handleClick} >1</Button>
            <Button value="2" onClick={handleClick} >2</Button>
            <Button value="3" onClick={handleClick} >3</Button>
            <Button value="+" onClick={handleClick} >+</Button>
            </div>
            <div className="row">
            <LongButton value="0" onClick={handleClick} >0</LongButton>
            <Button value="." onClick={handleClick} >.</Button>
            <Button onClick={handleAnswer} > Ans </Button>
            </div>
            </Card>
        </Container>
)
})

export default NumpadComponent
