     <h1 className="rgb">โปรแกรมคิดเลข </h1>
     <p>Number 1 : <input  value={num1} onChange={e => setNum1(+e.target.value)}/> </p> 
     <p>Number 2 : <input  value={num2} onChange={e => setNum2(+e.target.value)}/> </p>
     <h4>Cal :{num1} + {num2} </h4>
     <button onClick={Plus}>Plus</button> 
     <h4>Result : {total}</h4>

     </div>
     <hr/>

     <h4>Count: {counter}</h4>
     {/* <button onClick={() => setCount((prevCount) => prevCount + 1)}>
        count ++++
      </button>
      <h2>Count: {count}</h2>
      {renderList} */}
      <>
        {/* <h2>Count: {count}</h2>
         */}
        {/* <button onClick={decrement}>-</button>
        <button onClick={incrementOtherCounter}>not use useCallback</button> */}
      </>


    </div>
  );
}

export default App;
