import { useNumpad } from '../useNumpad';
import styled ,{ css }from 'styled-components';

const NumpadComponent = (() =>{

    const {Numpad, handleClick, input, handleAnswer, handleClear}= useNumpad();

    const Container = styled.div`
        text-align: center;
    `
    const Card = styled.div`
        box-shadow: 0 4px 8px 0 rgba(51, 50, 50, 0.2);
        max-width: 360px;
        margin: 5px;
        padding: 5px;
        text-align: center;
        background-color: #eaecec;
        border-radius: 20px;
    `
    const Screen = styled.div`
        height: 30px;
        background-color: #fafcfc;
        margin: 5px;
        padding: 10px 10px 0 10px;
        color: #070707;
        text-align: right;
        border-radius: 20px;

    `
    const Button = styled.button`
        background: transparent;
        border-radius: 3px;
        border: 2px solid palevioletred;
        color: palevioletred;
        margin: 0.5em 1em;
        padding: 0.25em 1em;

          ${(props: { primary: any; }) => 
            props.primary && css`
            background: palevioletred;
            color: white;
            `}
    `
    const TomatoButton = styled(Button)`
        background: tomato;
    `
    return (
        <Container> 
            <Card>
                <h2>Faiichan Calculate</h2>
            <Screen>
                {input}
            </Screen>
            <div className="row">
            <button onClick={handleClear} >C</button>
            <button value="%" onClick={handleClick}>%</button>
            <button value="/" onClick={handleClick}>/</button>
            </div>
            <div className="row">
            <button value="7" onClick={handleClick} >7</button>
            <button value="8" onClick={handleClick} >8</button>
            <button value="9" onClick={handleClick} >9</button>
            <button value="*" onClick={handleClick} >X</button>
            </div>
            <div className="row">
            <button value="4" onClick={handleClick} >4</button>
            <button value="5" onClick={handleClick} >5</button>
            <button value="6" onClick={handleClick} >6</button>
            <button value="-" onClick={handleClick} >-</button>
            </div>
            <div className="row">
            <button value="1" onClick={handleClick} >1</button>
            <button value="2" onClick={handleClick} >2</button>
            <button value="3" onClick={handleClick} >3</button>
            <button value="+" onClick={handleClick} >+</button>
            </div>
            <div className="row">
            <button value="0" onClick={handleClick} >0</button>
            <button value="." onClick={handleClick} >.</button>
            <button onClick={handleAnswer} > Ans </button>
            </div>
            </Card>
        </Container>
)
})

export default NumpadComponent
