import React from 'react';
// import logo from './logo.svg';
// import './App.css';
import{ useEffect, useState} from 'react';
import Home from 'Home';

function App() {
  const [homeName, setHomeName] = useState<string>("");
  const [isClose, setIsClose] = useState<boolean>(false);
  console.log("isClose :" , isClose);

  useEffect(() => {
    console.log("render");
    setHomeName("Is use effect" + (Math.random() * 99).toFixed(2));
  },[]);

  const [num1, setNum1] = useState(0);
  const [num2, setNum2] = useState(0);
  const [total, setTotal] = useState(num1 + num2);
  function Plus() {
    setTotal(num1 + num2);
  }
  console.log(Plus)
  return (
    <div className="App">
      <Home window={5} homeName={homeName}/>
      <input onChange={({ target: { value } }) => setHomeName(value)}/>
     <button onClick={() => setIsClose((prevState) => !prevState)}>Close</button>

     <hr/>
     <div style={{alignItems: 'center'}}>
     <h1>โปรแกรมคิดเลข </h1>
     <p>Number 1 : <input type="number" value={num1} onChange={e => setNum1(+e.target.value)}/> </p> 
     <p>Number 2 : <input type="number" value={num2} onChange={e => setNum2(+e.target.value)}/> </p> <br/>
     <button onClick={Plus}>Plus</button> 
     <h4>Result : {total}</h4>
     </div>

    </div>
  );
}

export default App;
