import { useContext, useState } from 'react'

const StudentInput =(() =>{
    const {studentList, setStudentList, addStudent }= useContext(AppContext);
    const [id, setId] = useState<number | string >();
    const [name, setName] = useState<string>("");
    return (
        <div>
    <div>
        <span>ID : </span>
        <input value={id} onChange = { ({target: {value}}) =>  {setId(value)} }></input>
    </div>
        <div>
        <span>Name : </span>
        <input value={name} onChange = { ({target: {value}}) =>  {setName(value)} }></input>
    </div>
        <button onClick={() =>{ addStudent({id,name}) } }>ADD</button>
    </div>
    )
})

export default StudentInput
